class Solution {
public:
    #define ll long long int
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        ll ans = 0, mi = 0, ma = 0;
        vector<ll> diff(n,0);
        for(int i=0;i<n;i++) diff[i] = (ll)target[i]-(ll)nums[i];
        for(int i=0;i<n;i++){
            int j = i;
            if(diff[i]>=0){
                while(j<n && diff[j]>=0) j++;
                j--;
                ans+=diff[i];
                for(int k=i+1;k<=j;k++){
                    ans+=max(diff[k]-diff[k-1],0LL);
                }
            }
            else{
                while(j<n && diff[j]<0) j++;
                j--;
                ans+=abs(diff[i]);
                for(int k=i+1;k<=j;k++){
                    ans+=max(abs(diff[k])-abs(diff[k-1]),0LL);
                }
            }
            i = j;
        }
        return ans;
    }
};
