import java.util.*;

class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        long[] speedDiff = new long[n];

        for (int i = 0; i < n; i++) {
            speedDiff[i] = (long)target[i] - nums[i];
        }

        long ops = 0;
        long currentSpeedDiff = 0;

        for (int i = 0; i < n; i++) {
            if (i == 0 || (speedDiff[i - 1] > 0 && speedDiff[i] > 0) || (speedDiff[i - 1] < 0 && speedDiff[i] < 0)) {
                long add = Math.abs(speedDiff[i]) - Math.abs(currentSpeedDiff);
                if (add > 0) {
                    ops += add;
                }
                currentSpeedDiff = speedDiff[i];
            } else {
                ops += Math.abs(speedDiff[i]);
                currentSpeedDiff = speedDiff[i];
            }
        }

        return ops;
    }
}
