class Solution:
    def minimumOperations(self, a: List[int], t: List[int]) -> int:
        d = [*map(sub, t, a)]
        a = [v for v,_ in groupby(d)]
        pd = 0
        res = 0
        for d in a:
            if pd > 0 and d > 0:
                if d >= pd:
                    res += d - pd
                pd = d
            elif pd < 0 and d < 0:
                if d <= pd:
                    res += pd - d
                pd = d
            else:
                res += abs(d)
                pd = d

        return res
