class Solution {
public:
    int min_operations(vector<int>& A)  
    {  
        if (A.size() == 0)  
            return 0;  

        if(A[0] < 0)
            for(auto &ele: A)
                ele *= -1;

        long long ans = A[0];  

        for (int i = 1; i < A.size(); i++)  
            ans += max(A[i] - A[i - 1], 0);  

        return ans;  
    }  
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long count = 0;
        vector<int> arr, v;

        for(int i=0;i<n;i++)
            arr.push_back(target[i]-nums[i]);

        for (int i = 0; i < n; i++) 
        {

            if(!v.size())
            {
                v.push_back(arr[i]);
                continue;
            }

            while(i<n && ((v.back()<0 && arr[i] < 0) || (v.back()>=0 && arr[i]>=0)))
                v.push_back(arr[i++]);

            count += min_operations(v);
            i--;
            v.clear();
        }

        if(v.size())
            count += min_operations(v);

        return count;
    }
};
