class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int l = nums.size();
        vector<int>difnums(l);
        vector<int>diftarget(l);
        difnums[0] = nums[0];
        for(int i=1;i<l;i++) difnums[i] = nums[i] - nums[i-1];

        //for(int i=0;i<l;i++) cout<<difnums[i]<<" ";

        //cout<<endl;

        diftarget[0] = target[0];
        for(int i=1;i<l;i++) diftarget[i] = target[i] - target[i-1];

        //for(int i=0;i<l;i++) cout<<diftarget[i]<<" ";
       // cout<<endl;
        long long zheng = 0;
        long long fu = 0;
        long long ans = 0;
        for(int i=0;i<l;i++){
            if(difnums[i] - diftarget[i] < 0){
                zheng += diftarget[i] - difnums[i];
            }else if(difnums[i] - diftarget[i] > 0){
                fu += difnums[i] - diftarget[i];
            }
        }
        // long long difleft = difnums[0] -diftarget[0];
        // long long difright = difnums[l-1] - difright[l-1];
        ans += max(fu,zheng);
        return ans;
    }
};
