class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        int n=nums.size(),i=0;

        while(i<n){
            if(nums[i]==target[i]){ i++; continue;}

            else if(nums[i]>target[i]){
                int j=i,mini=INT_MAX;
                while(j<n && nums[j]>target[j]){
                    mini=min(mini,nums[j]-target[j]);
                    j++;
                }
                for(int k=i;k<j;k++) nums[k]-=mini;
                ans+=mini;
            }

            else{
                int j=i,mini=INT_MAX;
                while(j<n && nums[j]<target[j]){
                    mini=min(mini,target[j]-nums[j]);
                    j++;
                }
                for(int k=i;k<j;k++) nums[k]+=mini;
                ans+=mini;
            }
        }
        return ans;
    }
};
