class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        a = [0] * n
        for i in range(n):
            a[i] = target[i] - nums[i]
        ans = abs(a[0])
        last = a[0]
        for i in range(1, n):
            if a[i] > 0 and last > 0:
                if a[i] > last:
                    ans += a[i] - last
                last = a[i]
            elif a[i] < 0 and last < 0:
                if a[i] < last:
                    ans += abs(last - a[i])
                last = a[i]
            else:
                ans += abs(a[i])
                last = a[i]

        return ans
