class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = []
        for n, t in zip(nums,target):
            diff+=[n-t]

        k = 0
        count = 0
        for d in diff:
            if k*d<=0:
                count += abs(d)
                k = d
            elif abs(d)<abs(k):
                k = d
            else:
                count += abs(d)-abs(k)
                k = d
        return count
