class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<long long> d(n);

        for (int i = 0; i < n; ++i)
            d[i] = nums[i] - target[i];

        long long ans = 0;
        long long sign = 0;
        long long mx = 0;

        for (int i = 0; i < n; ++i) {
            long long sign2 = d[i]>0 ? 1 : (d[i]? -1 : 0);
            if (sign != sign2) {
                ans += mx;
                mx = 0;
            }

            long long v = abs(d[i]);
            if (v < mx) {
                ans += mx - v;
                mx = v;
            } else {
                mx = v;
            }
            sign = sign2;
        }

        ans += mx;

        return ans;
    }
};
