class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        int n = nums.size();
        vector<int> c(n+1,0);
        c[0] = (nums[0] - target[0]);
        for (int i = 1; i <n; ++i)c[i]=(nums[i] - target[i])-((nums[i-1] - target[i-1]));

        long long x=0,y=0;
        for(int i=0;i<n;i++){
             if(c[i]>0)x+=c[i];
            else y-=c[i];
        }
        cout<<x<<" "<<y<<endl;
        return max(x,y);
    }
};
