#define ll long long
class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& t) {
        int n=t.size();
        ll d=nums[0]-t[0];
        ll d1=d;
        ll ans=0;
        //cout<<d<<" ";
        for(int i=1;i<n;i++){
            if(nums[i]-t[i]!=d){
                int d1=nums[i]-t[i];
                if((d1>0&&d>0)||(d1<0&&d<0))
                {   if(abs(d1)<=abs(d))
                    d1=0;
                    else
                    d1=abs(d-d1);
                }
                else{
                    d1=abs(d1); 
                }
                ans+=d1;
                d=nums[i]-t[i];
                //cout<<d<<" d ";
                //cout<<i<<" ";
            }
        }
        ans+=abs(d1);
        //cout<<"\n";
        return ans;
    }
};
