class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> vec;
        long long res=0;
        for(int i=0;i<nums.size();i++) vec.push_back(nums[i]-target[i]);
        for(int i=0;i<vec.size();i++){
            if(vec[i]>0){
                int t=i;
                while(i+1<vec.size() && vec[i+1]>0){
                    i++;
                }
                // cout << t << " " << i << endl;
                res+=solveSub(vec,t,i);
            }
            else if(vec[i]<0){
                vec[i]=abs(vec[i]);
                int t=i;
                while(i+1<vec.size() && vec[i+1]<0){
                    vec[i+1]=abs(vec[i+1]);
                    i++;
                }
                // cout << t << " " << i << endl;
                res+=solveSub(vec,t,i);
            }
        }
        return res;
    }

private:
    long long solveSub(vector<int>& vec, int a, int b){
        if(a==b) return vec[a];
        int mx=vec[a];
        long long res=0;
        for(int i=a+1;i<=b;i++) mx=min(mx,vec[i]);
        // cout << "mx: " << mx << endl;
        for(int i=a;i<=b;i++) vec[i]-=mx;
        // for(int i=a;i<=b;i++) cout << vec[i] << endl;
        res+=mx;
        for(int i=a;i<=b;i++){
            if(vec[i]>0){
                if(i+1<=b && vec[i+1]==0) res+=vec[i];
                else{
                    int y=i;
                    while(i+1<=b && vec[i+1]>0){
                        i++;
                    }
                    res+=solveSub(vec,y,i);
                }
            }
        }
        return res;
    }
};
