class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n = (int)a.size();
        for (int i = 0; i < n; i++) {
            a[i] -= b[i];
        }
        long long answer = 0;
        int cur = 0;
        for (int i = 0; i < n; i++) {
            if (a[i] >= 0) {
                if (cur >= 0) {
                    if (a[i] > cur) {
                        answer += a[i] - cur;
                    }
                } else {
                    answer += a[i];
                }
            } else {
                if (cur <= 0) {
                    if (a[i] < cur) {
                        answer += cur - a[i];
                    }
                } else {
                    answer -= a[i];
                }
            }
            cur = a[i];
        }
        return answer;
    }
};
