class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        l = len(nums)
        sys.setrecursionlimit(10000)

#         dp = [[0]*l for i in range(l)]

#         for i in range(l):
#             dp[i][i] = abs(nums[i] - target[i])




#         print(dp)

        def cal(ns):
            #print('cal_ns', ns)
            if len(ns) == 0:
                return 0

            if len(ns) == 1:
                return abs(ns[0])

            min_n = min(ns)
            max_n = max(ns)

            if min_n > 0:
                new_ns = list(ns)
                for i in range(len(ns)):
                    new_ns[i] = ns[i] - min_n
                return cal(new_ns) + min_n

            if max_n < 0:
                new_ns = list(ns)
                for i in range(len(ns)):
                    new_ns[i] = ns[i] - max_n
                return cal(new_ns) - max_n

            prev = ns[0]

            ans = 0
            new_ns = [ns[0]]
            if ns[0] == 0:
                new_ns = []

            for n in ns[1:]:
                if n > 0 and prev >= 0:
                    new_ns.append(n)
                elif n > 0 and prev < 0:
                    ans += cal(new_ns)
                    new_ns = [n]
                    prev = n
                elif n == 0:
                    ans += cal(new_ns)
                    new_ns = []
                elif n < 0 and prev <= 0:
                    new_ns.append(n)
                elif n < 0 and prev > 0:
                    ans += cal(new_ns)
                    new_ns = [n]
                    prev = n

            return ans + cal(new_ns)

        diffs = [0]*l
        for i in range(l):
            diffs[i] = target[i] - nums[i]

        print(diffs)

        return cal(diffs)
