#include<bits/stdc++.h>
using namespace std;
class Solution {
public:
    typedef long long ll;
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        ll sum = 0;
        for(int i = 0; i < nums.size(); i ++) {
            if(i == 0) {
                sum += abs(nums[i] - target[i]);
            } else {
                if(1ll * (nums[i] - target[i]) * (nums[i - 1] - target[i - 1]) >= 0) {
                    int cur_operations = abs(nums[i] - target[i]);
                    int front_operations = abs(nums[i - 1] - target[i - 1]);
                    if(cur_operations <= front_operations) {
                        continue;
                    } else {
                        sum += cur_operations - front_operations;
                    }
                } else {
                    sum += abs(nums[i] - target[i]);
                }
            }
        }
        return sum;
    }
};
