class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& tar) {
        long long ans=0;
        int n=nums.size();
        vector<int> diff(n);
        for(int i=0;i<n;i++) diff[i]=nums[i]-tar[i];

        ans=abs(diff[0]);

        for(int i=1;i<n;i++){
            if(diff[i]>0){
                if(diff[i-1]==0 || diff[i-1]<0){
                    ans+=abs(diff[i]);
                }else{
                    if(abs(diff[i])>abs(diff[i-1])) ans+=abs(diff[i])-abs(diff[i-1]);
                }   
            }else if(diff[i]<0){
                if(diff[i-1]==0 || diff[i-1]>0){
                    ans+=abs(diff[i]);
                }else{
                    if(abs(diff[i])>abs(diff[i-1])) ans+=abs(diff[i])-abs(diff[i-1]);
                }  
            }
        }
        return ans;
    }
};
