class Solution {
public:
    long long minimumOperations(vector<int>& a, vector<int>& b) {
        int n=a.size();
        vector<int> c(n);
        for(int i=0;i<n;i++){
            c[i]=a[i]-b[i];
            // cout<<c[i]<<" ";
        }
        // cout<<endl;
        long long ans=0;
        int i=0;
        while(i<n){
            if(c[i]==0) i++;
            else{
                if(c[i]>0){
                    int j=i;
                    int mn=c[i];
                    while(j<n&&c[j]>0){
                   mn=min(mn,c[j]);
                        j++;

                    }
                    j--;
                    for(int k=i;k<=j;k++) c[k]=c[k]-mn;
                    ans+=abs(mn);
                    // cout<<"ans "<<ans<<endl;
                }else{
                    int j=i;
                    int mn=abs(c[i]);
                    while(j<n&&c[j]<0){
                        mn=min(mn,abs(c[j]));
                        j++;
                    }
                    j--;
                    for(int k=i;k<=j;k++) c[k]=c[k]+mn;
                    ans+=abs(mn);
                }
            }
        }
        return ans;
    }
};
