class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        for(int i=0;i<n;i++){
            nums[i]-=target[i];
        }
        nums.push_back(0);
        reverse(nums.begin(),nums.end());
        long long res=0;
        stack<int> stk;
        int last=0;
        for(auto num: nums){
            if((num>=0 && last<0) || (num<=0 && last>0)){
                while(!stk.empty()){
                    stk.pop();
                }
            }
            if(stk.empty()){
                stk.push(num);
                res+=abs(num);
            }
            else if(abs(stk.top())>abs(num)){
                stk.push(num);
            }
            else{
                res+=(abs(num)-abs(stk.top()));
                stk.pop();
                while(!stk.empty() && abs(stk.top())<abs(num)){
                    stk.pop();
                }
                stk.push(num);
            }
            last=num;
        }
        return res;
    }
};
