typedef long long ll;
class Solution {
public:
    ll minimumOperations(vector<int> &nums, vector<int> &target) {
        ll result = 0, current = 0;
        vector<int> differences;

        for(int i = 0; i < nums.size(); i++) {
            differences.push_back(nums[i] - target[i]);
        }

        for(int i = 0; i < nums.size(); i++) {
            if(i == 0 || (differences[i-1] > 0 && differences[i] > 0) || (differences[i-1] < 0 && differences[i] < 0)) {
                int add = abs(differences[i]) - abs(current);
                if(add > 0) {
                    result += add;
                }
            } else {
                result += abs(differences[i]);
            }
            current = differences[i];
        }

        return result;
    }
};
