class Solution {
    #define ll long long
public:
    int f(int l,int r,vector<int>& v)
    {
        int cnt = abs(v[l]);
        for (int i = l+1; i <= r; i++)
            cnt += max(abs(v[i]) - abs(v[i - 1]), 0);
        return cnt;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int> v;
        for(int i=0;i<n;i++) {
            v.push_back(-nums[i]+target[i]);
        }
        long long sum=0,a=0;
        for(int i=0;i<n;i++) {
            if((v[a]>=0 && v[i]<0) || (v[a]<0 && v[i]>=0)) {
                sum+=f(a,i-1,v);
                a=i;
            }
        }
        sum+=f(a,n-1,v);
        return sum;
    }
};
