typedef long long ll;

class Solution {
public:
    long long minimumOperations(vector<int> &nums, vector<int> &target) {
        for (int i = 0; i < target.size(); ++i) {
            target[i] -= nums[i];
        }
        vector<ll> s;
        for (int i = 0; i < target.size(); ++i) {
            if (i == 0)s.push_back(target[i]);
            else {
                ll tmp = target[i] - target[i - 1];
                if (tmp == 0)continue;
                s.push_back(tmp);
            }
        }
        ll ret = 0;
        ll cntadd = 0, cntminus = 0;

        for (auto &i: s) {
            // cout << "add: " << cntadd << " minus: " << cntminus << endl;
            if (i > 0) {
                if (abs(cntminus) >= i) {
                    cntminus -= i;
                } else {
                    i -= cntminus;
                    ret += abs(i);
                    cntminus = 0;
                    cntadd += i;
                }
            } else {
                if (abs(cntadd) >= abs(i)) {
                    cntadd -= abs(i);
                } else {
                    i += cntadd;
                    ret += abs(i);
                    cntadd = 0;
                    cntminus += -i;
                }
            }
        }
        return ret ;
    }
};

