class Solution {

    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        int[] diff1 = new int[n + 1], diff2 = new int[n + 1];        
        for(int i = 0; i < n; i++){
            diff1[i] += nums[i];
            diff1[i + 1] -= nums[i];
            diff2[i] += target[i];
            diff2[i + 1] -= target[i];
        }
        // System.out.println(Arrays.toString(diff1));
        // System.out.println(Arrays.toString(diff2));
        long pos = 0, neg = 0;
        long ans = 0;
        for(int i = 0; i < n; i++){
            if(diff1[i] == diff2[i]) {
                continue;
            }
            else if(diff1[i] < diff2[i]){
                // pos
                long a = diff2[i] - diff1[i];
                long want = a - Math.min(a, neg);
                neg -= Math.min(a, neg);
                ans += want;
                pos += want;
                //System.out.println(i + " " + ans + " " + want + " " + pos + " " + neg);
            }
            else { 
                // neg
                long a = diff1[i] - diff2[i];
                long want = a - Math.min(a, pos);
                pos -= Math.min(a, pos);
                ans += want;
                neg += want;
                //System.out.println(i + " " + ans + " " + want + " " + pos + " " + neg);
            }
        }
        return ans;
    }

}
