class Solution {
public:
    int rec(long long k, vector<int> &u, vector<int> &v){
        long long t=0, r=0;
        for(int i=0; i<u.size(); i++) {
            if(u[i]<=v[i]){
                if(t==0) t=1, r=0;
                if(r>=v[i]-u[i]) r=v[i]-u[i];
                else k+=r, r=v[i]-u[i], k-=r;
                // if(t==-1) t=1;
            } 
            else if(v[i]<u[i] ) {
                if(t==1) t=0, r=0;
                if(r>=u[i]-v[i]) r=u[i]-v[i];
                else  k+=r, r=u[i]-v[i], k-=r;
                // if(t==-1) t=0;
            }
            if(k<0) return 0;
        }
        return 1;
    }
    long long minimumOperations(vector<int>& u, vector<int>& v) {
        long long r, l=0, m, t=0;
        for(int i=0; i<u.size(); i++) t+=abs(v[i]-u[i]); r=t;
        while(l<=r){
            m=(l+r)/2;
            if(rec(m, u, v)) r=m-1, t=m; else l=m+1;
        }
        return t;
    }
};
