class Solution {
public:
    long long fun(vector<long long>& v){
        if(v.size() == 0) return 0;
        int m = v.size();
        for(int i=0;i<m;i++) {
            if(v[i]<0) v[i] *=-1;
        }
        vector<int> st;
        int res = 0;

        st.push_back(v[0]);
        for (int i = 1; i < m; i++) {
            if (v[i] > st.back()) {
                st.push_back(v[i]);
                continue;
            } 
            if (v[i] == st.back())
                continue;

            int top = st.back();

            while (!st.empty() && v[i] < st.back())
                st.pop_back();

            res += top - v[i];
            st.push_back(v[i]);
        }

        if (!st.empty())
            res += st.back();

        return res;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        long long n = nums.size();
        int i=0;
        long long val = 0;
        vector<long long>diff(n);
        vector<long long>p;
        for(int i=0;i<n;i++){
            diff[i] = target[i]-nums[i];
        }   

        for(int i=0;i<n;i++){
            if(p.size() == 0){
                if(diff[i] != 0) p.push_back(diff[i]);
            }
            else{
                if((diff[i] >0 && p.back()>0 )|| (diff[i] <0 && p.back()<0)){
                    p.push_back(diff[i]);
                }
                else{
                    ans += fun(p);
                    p.clear();
                    if(diff[i] != 0) p.push_back(diff[i]);
                }
            }
        }
        ans += fun(p);
        return ans;
    }
};
