class Solution {
public:
    long long  wans(vector<int>v){
        long long count=0;
        stack<int> s;
        for(int i=0;i<v.size();i++){
            if(s.size()==0) s.push(v[i]);
            else{
                   int x=s.top();
                    while(s.size()!=0 && s.top()>=v[i]) {
                        s.pop();
                    }
                    count+=max(0,x-v[i]);
                    // if(s.size()!=0 || s.top()==v[i]) continue;
                    s.push(v[i]);
            }
        }
        if(s.size()!=0) count+=s.top();
        return count;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        vector<int> a;
        for(int i=0;i<nums.size();i++){
            a.push_back(target[i]-nums[i]);
        }
        int i=0;
        int n=a.size();
        long  long ans=0;
        while(i<n){
            int x=a[i];
            if(x==0){
                i++;
                continue;
            }
            if(x>0){
                vector<int> v;
                while(i<n &&a[i]>0){
                    v.push_back(a[i]);
                    i++;
                }
                ans+=wans(v);
            }
            else{
                vector<int> v;
                while(i<n &&a[i]<0){
                    v.push_back( -a[i]);
                    i++;
                }
                ans+=wans(v);
            }
        }
        return ans;
        // [9,2,6,10,4,8,3,4,2,3]
        // [9,5,5,1,7,9,8,7,6,5]
    }
};
