class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:

        def helper(arr):
            stack = []
            print(arr)
            ans = 0
            stack.append(arr[0])
            for i in range(1,len(arr)):
                while arr[i] < stack[-1]:
                    val = stack.pop()
                    ans += val - max(stack[-1],arr[i])
                if stack[-1] == arr[i]:
                    continue
                stack.append(arr[i])
            # return min operation
            return ans

        pos = [0]
        neg = [0]

        for i in range(len(nums)):
            val = target[i] - nums[i]
            if val >= 0:
                if neg[-1] != 0:
                    neg.append(0)
                pos.append(val)
            else:
                if pos[-1] != 0:
                    pos.append(0)
                neg.append(-val)

        if neg[-1] != 0:
            neg.append(0)
        if pos[-1] != 0:
            pos.append(0)

        ans = helper(pos) + helper(neg)
        return ans
