class Solution {
public:

    long long minimumOperations(vector<int>& v, vector<int>& t) {
        int n = v.size();

        long long ret = 0;
        int val = 0;

        int i=0;
        while(i<n){
            long long val = t[i] - v[i];
            if(val == 0){
                i++; 
                continue;
            }
            int s = 1;
            if(val<0)s = -1;
            long long int cur = abs(val);
            ret +=abs(val);
            int j = i+1;
            while(j<n && (((t[j]-v[j])*s)>=1) ){
                long long int dif = abs(t[j]-v[j]);
                if(dif >cur){
                    ret +=(dif-cur);
                    cur = dif;
                } else{
                    cur = dif;
                }
                j++;
            }
            i=j;
        }
        return ret;
    }
};
