class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        // map<int,vector<pair<int,int>>>mp;
        // int ct=0;
        // int s=0;
        // int gp=nums[0]-target[0];
        // int n=nums.size();
