        //     for(auto y:x.second){
        //         ans+=(abs(nums[y.first]-target[y.first]));
        //     }
        // }
        // return ans;

        int mxg=0;
        int n=nums.size();
        int dr=1;
        long long ans=0;
        for(int x=0;x<n;x++){
            if(target[x]-nums[x]>=0 && dr==1){
                if(target[x]-nums[x]>=mxg){
                    ans+=(target[x]-nums[x])-mxg;
                    mxg=target[x]-nums[x];
                }else{
                    mxg=target[x]-nums[x];
                }
            }else if(target[x]-nums[x]>=0 && dr==0){
                dr=1;
                mxg=0;
                x--;
            }else if(target[x]-nums[x]<=0 && dr==0){
                if(nums[x]-target[x]>mxg){
                    ans+=(nums[x]-target[x])-mxg;
                    mxg=nums[x]-target[x];
                }else{
                    mxg=nums[x]-target[x];
                }
            }else if(target[x]-nums[x]<=0 && dr==1){
                dr=0;
                mxg=0;
                x--;
            }
        }
        return ans;
    }
};
