class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        vector<int> diff(n, 0);
        for(int i=0;i<n;i++)
            diff[i] = target[i] - nums[i];
        vector<pair<int, int>> seggs;
        int st = 0;
        for(int i=1;i<n;i++){
            if((diff[i] >= 0 && diff[st] >= 0) || (diff[i] < 0 && diff[st] < 0))
                continue;
            else{
                seggs.push_back({st, i-1});
                st = i;
            }
        }
        seggs.push_back({st, n-1});
        long long ans = 0;
        for(auto [x,y] : seggs){
            if(diff[x] < 0) diff[x] = -diff[x];
            ans += diff[x];
            for(int i=x+1;i<=y;i++){
                if(diff[i] < 0) diff[i] = -diff[i];
                ans += max(diff[i] - diff[i-1], 0);
            }
        }
        return ans;
    }
};
