class Solution {
public:
    long long help(vector<int> &v){
        for(int i=0;i<v.size();i++){
            // cout<<v[i]<<" ";
            if(v[i] < 0){
                v[i] = (v[i]*-1);
            }
        }
        // cout<<endl;

        long long res = abs(v[0]);
        for(int i=1;i<v.size();i++){
            if(v[i] > v[i-1]){
                res += (v[i]-v[i-1]);
            }
        }
        return res;
    }
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        vector<int> diff;
        for(int i=0;i<n;i++){
            diff.push_back(nums[i] - target[i]);
        }
        vector<vector<int>> stor;
        vector<int> temp;temp.push_back(diff[0]);
        // cout<<temp[0]<<endl;
        for(int i=1;i<diff.size();i++){
            if(diff[i] == 0){
                if(temp.size() > 0) stor.push_back(temp);
                vector<int> newer;temp = newer;
            } else if(temp.size() == 0) {
                temp.push_back(diff[i]);
            } else if(diff[i]>0 && temp.back() > 0){
                temp.push_back(diff[i]);
            } else if(diff[i]<0 && temp.back() < 0){
                temp.push_back(diff[i]);
            } else {
                stor.push_back(temp);
                vector<int> newer;newer.push_back(diff[i]);
                temp = newer;
            }
        }
        if(temp.size() > 0){
            stor.push_back(temp);
        }

        for(auto x:stor){
            ans += help(x);
        }
        return ans;

    }
};
