class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        long ans = 0;
        int n = nums.length;
        int[] a = new int[n];
        for(int i = 0; i < n; ++i) {
            a[i] = Math.max(nums[i] - target[i], 0);
        }
        ans += f(a);
        Arrays.fill(a, 0);
        for(int i = 0; i < n; ++i) {
            a[i] = -Math.min(nums[i] - target[i], 0);
        }
        ans += f(a);
        return ans;
    }

    long f(int[] a) {
        long res = 0;
        res += a[0];
        for(int i = 1; i < a.length; ++i) {
            if(a[i] > a[i - 1]) {
                res += a[i] - a[i - 1];
            }
        }
        return res;
    }
}
