class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long n = nums.size();
        vector<long long>diff(n, 0);
        for(int i=0; i<n; i++){
            diff[i]=target[i]-nums[i];
        }

        long long ans = 0;
        long long curr = 0;
        bool shift=1;
        for(int i=0; i<n; i++){
            if(diff[i]>0 && shift==0){
                shift=1;
                curr=0;
            }
            if(diff[i]<0 && shift){
                shift=0;
                curr=0;
            }
            if(diff[i]<0 && shift==0){
                diff[i]= -diff[i];
            }
            if(diff[i]>curr){
                ans+=(diff[i]-curr);
            }
            curr=diff[i];
        }
        return ans;
    }
};
