class Solution {
    public long minimumOperations(int[] nums, int[] target) {
        int n = nums.length;
        int[] difference = new int[n];

        // Calculate the difference between target and nums
        for (int i = 0; i < n; i++) {
            difference[i] = target[i] - nums[i];
        }

        int opr = 0;
        int prev = 0;
        long ans = 0;

        for (int i = 0; i < n; i++) {
            if (difference[i] == 0) {
                prev = 0;
                opr = 0;
            } else if (difference[i] > 0) {
                if (prev == 1) {
                    int need = difference[i];
                    if (need == opr) continue;
                    else if (need < opr) {
                        opr = need;
                    } else {
                        ans += (need - opr);
                        opr = need;
                    }
                } else {
                    int need = difference[i];
                    opr = need;
                    ans += opr;
                    prev = 1;
                }
            } else {
                if (prev == 0) {
                    int need = Math.abs(difference[i]);
                    if (opr == need) continue;
                    else if (opr > need) opr = need;
                    else {
                        ans += (need - opr);
                        opr = need;
                    }
                } else {
                    prev = 0;
                    int need = Math.abs(difference[i]);
                    ans += need;
                    opr = need;
                }
            }
        }

        return ans;
    }
}
