class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n = nums.size();
        long long operate = 0;
        long long amrname = 0;
        vector<int> d(n);
        for(int i=0;i<n;i++){
            d[i]=target[i]-nums[i];
        }
        operate = d[0];
        amrname = abs(operate);

        for(int i=1;i<n;i++){
            if(d[i]>=d[i-1]){
                operate = d[i];
                if(d[i]>0){
                    amrname+=min(d[i] , d[i]-d[i-1]);
                }
            }
            else{
                operate = d[i];
                if(d[i]<0){
                    amrname+=min(abs(d[i]) , abs(d[i-1]-d[i]));
                }
            }
        }
        return amrname;
    }
};
