from typing import List

class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diffs = [target[i] - nums[i] for i in range(len(nums))]       
        ops = 0
        prev_diff = 0

        for i in range(len(diffs)):
            if i == 0 or (diffs[i - 1] > 0 and diffs[i] > 0) or (diffs[i - 1] < 0 and diffs[i] < 0):
                add_ops = abs(diffs[i]) - abs(prev_diff)
                if add_ops > 0:
                    ops += add_ops
                prev_diff = diffs[i]
            else:  
                ops += abs(diffs[i])
                prev_diff = diffs[i]

        return ops
