class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        d = [0] * n

        for i in range(n):
            d[i] = nums[i] - target[i]

        dd = [0] * n # dd 差分数组
        dd[0] = d[0]
        for i in range(n - 1):
            dd[i + 1] = d[i + 1] - d[i]

        # print("goal:", d)
        # print("diff:", dd)

        a = b = 0
        for x in dd:
            if x > 0:
                a += x
            else:
                b -= x

        return max(a, b)
