class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        for i in range(len(nums)):
            nums[i] = target[i] - nums[i]
        tot = 0
        prev = 0
        for i in range(len(nums)):
            old_prev = prev
            prev = nums[i]
            if nums[i] < 0:
                if old_prev < 0:
                    tot += abs(nums[i]) - min(abs(nums[i]), abs(old_prev))
                else:
                    tot += abs(nums[i])
            else:
                if old_prev > 0:
                    tot += nums[i] - min(nums[i], old_prev)
                else:
                    tot += nums[i]
        return tot
