class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        for i in range(len(nums)):
            nums[i] -= target[i]
        dp = [0] * len(nums)
        dp[0] = abs(nums[0])
        for i in range(1, len(dp)):
            if nums[i] * nums[i - 1] >= 0: #and abs(nums[i]) <= abs(nums[i - 1])
                if abs(nums[i]) > abs(nums[i - 1]):
                    dp[i] = dp[i - 1] + abs(nums[i]) - abs(nums[i - 1])
                else:
                    dp[i] = dp[i - 1]
            else:
                dp[i] = dp[i - 1] + abs(nums[i])
        return dp[-1]
