class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        d = []
        for x, y in zip(nums, target):
            d.append(x - y)
        diff = []
        diff.append(d[0])
        for i in range(1, len(d)):
            diff.append(d[i] - d[i - 1])
        # print(diff)
        t1, t2 = 0, 0
        for x in diff:
            if x > 0:
                t1 += x
            elif x < 0:
                t2 -= x
        return max(t1, t2)
