class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long n = nums.size();
        vector<long long> differ(n);
        for (long long i = 0; i < n; ++i) {
            differ[i] = target[i] - nums[i];
        }
        long long final_op = 0;
        long long differ_prt = 0;
        for (long long i = 0; i < n; ++i) {
            if (i == 0 || (differ[i - 1] > 0 && differ[i] > 0) || (differ[i - 1] < 0 && differ[i] < 0)) {
                long long extra_op = abs(differ[i]) - abs(differ_prt);
                if (extra_op > 0) {
                    final_op += extra_op;
                }
                differ_prt = differ[i];
            } else {
                final_op += abs(differ[i]);
                differ_prt = differ[i];
            }
        }
        return final_op;
