class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        diff = [target[i] - nums[i] for i in range(len(nums))]
        print(diff)
        ans = abs(diff[0])
        cur = abs(diff[0])
        sign = diff[0] > 0
        for i in range(1, len(diff)):
            if (diff[i] > 0) == sign:
                if abs(diff[i]) > abs(diff[i - 1]):
                    ans += abs(diff[i]) - abs(diff[i - 1])
                cur = abs(diff[i])
            else:
                sign = diff[i] > 0
                ans += abs(diff[i])
                cur  = abs(diff[i])
        return ans
