class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        ops = [0]*len(nums)
        for i in range(0, len(nums)):
            ops[i] = target[i] - nums[i]
        ops.append(0)
        print(ops)
        res = 0
        for i in range(1, len(ops)):
            if ops[i] == 0 or ops[i] * ops[i-1] < 0:
                res += abs(ops[i-1])
            else:
                if ops[i] > 0:
                    if ops[i] < ops[i-1]:
                        res += ops[i-1] - ops[i]

                else:
                    if ops[i] > ops[i-1]:
                        res += ops[i] - ops[i-1]
        return res
