class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        if(nums.size()!=target.size()){
            return -1;
        }
        long long ans=0;
        int n = nums.size();
        int curmx=0;
        for(int i=0;i<n;i++){
            int k = nums[i] - target[i];
            if(curmx==0 && k!=0){
                curmx = k;
            }
            else if(k>0 && curmx>0){
                if(k>=curmx){
                    curmx = k;
                }
                else{
                    ans+=(curmx-k);
                    curmx = k;
                }
            }
            else if(k<0 && curmx<0){
                if(k<=curmx){
                    curmx = k;
                }
                else{
                    ans+=(abs(curmx-k));
                    curmx = k;
                }
            }
            else if(k==0){
                ans+=(abs(curmx));
                curmx=0;
            }
            else{
                ans+=(abs(curmx));
                curmx = k;
            }
        }
        ans+=(abs(curmx));
        return ans;
    }
};
