class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long res = 0, prevSt = 0, curr;

        for(int i=0; i<nums.size(); i++) {
            curr = target[i] - nums[i];

            if(curr != prevSt) {

                if(curr < 0 && prevSt < 0) {
                    if(abs(curr) > abs(prevSt)) {
                        res += (abs(curr) - abs(prevSt));
                    }

                    prevSt = curr;
                }

                else if(curr >= 0 && prevSt >= 0) {
                    if(curr > prevSt) {
                        res += curr - prevSt;
                    }

                    prevSt = curr;
                }

                else {
                    res += abs(curr);
                    prevSt = curr;
                }
            }
        }

        return res;
    }
};
