class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans = 0;
        int n = nums.size();
        vector<int> diff(n);
        for(int i =0; i<n;i++){
            diff[i] = target[i] - nums[i];
        }
        long long sum = 0;
        int i = 0;
        while(i < n){
           if(i == 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i-1] < 0 && diff[i] < 0)){
               if((abs(diff[i]) - abs(sum)) > 0){
                   ans += abs(diff[i]) - abs(sum);
               }
               sum = diff[i];
           }
            else{
                ans += abs(diff[i]);
                sum = diff[i];
            }
            i++;
        }
        return ans;
    }
};
