class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        int n=nums.size();
        vector<int> final(n);
        for (int i=0;i<n;i++){
            final[i]=(target[i]-nums[i]);
        }
        long long ans=0;
        int extreme=final[0];
        ans+=abs(final[0]);
        for (int i=1;i<n;i++){
            if ((long long)extreme*final[i]<0){
                ans+=abs(final[i]);
                extreme=final[i];

                continue;
            }
            ans+=max(0,abs(final[i])-abs(extreme));
            extreme=final[i];

        }
        return ans;
    }
};
