class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& t) {
        vector<long long> diff(nums.size());
        for (size_t i = 0; i < nums.size(); ++i) {
        diff[i] = t[i] - nums[i];
        }
        long long to = 0;
        long long  cd = 0;

        for (size_t i = 0; i < diff.size(); ++i) {
            if (i == 0 || (diff[i - 1] > 0 && diff[i] > 0) || (diff[i - 1] < 0 && diff[i] < 0)) {
                int o = abs(diff[i]) - abs(cd);
                if (o > 0) {
                   to += o;
                }
                cd = diff[i];
            } else {
                   to += abs(diff[i]);
                   cd = diff[i];
            }
        }

        return to;
    }
};
