class Solution {
public:
    long long minimumOperations(vector<int>& nums, vector<int>& target) {
        long long ans=0;
        int n=nums.size();
        for(int i=0;i<n;++i){
            nums[i]=target[i]-nums[i];
            // printf(" %d: %d \n",i,nums[i]);
        }
        for(int i=0;i<n;++i){
            if (i==0) ans+=abs(nums[i]-0);
            else{
                ans+=abs(nums[i]-nums[i-1]);
            }
        }
        ans+=abs(0-nums[n-1]);
        return ans/2;
    }
};
