class Solution:
    def minimumOperations(self, nums: List[int], target: List[int]) -> int:
        n = len(nums)
        diff = [nums[i] - target[i] for i in range(n)]

        inter = [[diff[0]]]
        for i in range(1, len(diff)):
            if diff[i] * inter[-1][0] > 0:
                inter[-1].append(diff[i])
            else:
                inter.append([diff[i]])

        def countOps(arr: List[int]) -> int:
            arr = [(abs(arr[i]), i) for i in range(len(arr))]
            arr.sort()

            removed = set([-1, len(arr)])

            ans = 0
            for val, idx in arr:
                if idx - 1 in removed or idx + 1 in removed:
                    if idx - 1 in removed and idx + 1 in removed:
                        ans += val
                else:
                    ans -= val
                removed.add(idx)

            return ans

        ans = 0
        for arr in inter:
            ans += countOps(arr)

        return ans
